select auditId,CAST(AES_DECRYPT(a.userId, 'VmYq3t6w9y$B&E)H') AS CHAR) AS userId,CAST(AES_DECRYPT(userEmail, 'VmYq3t6w9y$B&E)H') AS CHAR) AS userEmail,concat(firstName,' ',lastName) as userName,CAST(AES_DECRYPT(workspaceId, 'VmYq3t6w9y$B&E)H') AS CHAR) AS workspace,CAST(AES_DECRYPT(changeEntityType, 'VmYq3t6w9y$B&E)H') AS CHAR) AS changeEntityType,CAST(AES_DECRYPT(changeEntityName, 'VmYq3t6w9y$B&E)H') AS CHAR) AS changeEntityName,CAST(AES_DECRYPT(operationType, 'VmYq3t6w9y$B&E)H') AS CHAR) AS operationType,CAST(AES_DECRYPT(valueUpdated, 'VmYq3t6w9y$B&E)H') AS CHAR) AS valueUpdated,cast(a.creationTimestamp as char) as creationTimestamp from autoDQ_UI.cfg_audit as a,autoDQ_UI.cfg_users as u where CAST(AES_DECRYPT(a.userId, 'VmYq3t6w9y$B&E)H') AS CHAR)=u.userId and CAST(AES_DECRYPT(objectId, 'VmYq3t6w9y$B&E)H') AS CHAR) = 31 ;